parameters.jsCode:
// La fecha de inicio debe ser un objeto Date
// Si el primer video se publica hoy, la inicializamos
let fechaActual = new Date(); 

const numeroDeVideos = $('Generador de Ideas').first().json['Cantidad Ideas'];
const fechasDePublicacion = [];

// Esta es la funci√≥n que calcula la pr√≥xima fecha de publicaci√≥n
function calcularProximaFecha(fechaBase, diaObjetivo) {
  let fecha = new Date(fechaBase);
  const diaSemanaActual = fecha.getDay(); // 0 = Domingo, 1 = Lunes, 4 = Jueves, etc.

  // Calculamos los d√≠as que faltan para llegar al d√≠a objetivo
  let diferenciaDias = diaObjetivo - diaSemanaActual;

  // Si la fecha actual ya es posterior al d√≠a objetivo, sumamos 7 para ir a la siguiente semana
  if (diferenciaDias <= 0) {
    diferenciaDias += 7;
  }

  fecha.setDate(fecha.getDate() + diferenciaDias);
  return fecha;
}

// L√≥gica principal
for (let i = 0; i < numeroDeVideos; i++) {
  // Verificamos si la fecha actual es un lunes (1) o un jueves (4)
  const diaSemana = fechaActual.getDay();

  if (diaSemana !== 1 && diaSemana !== 4) {
    // Si la fecha actual no es ni lunes ni jueves, la ajustamos al pr√≥ximo d√≠a de publicaci√≥n
    if (diaSemana < 1 || (diaSemana > 1 && diaSemana < 4)) {
      fechaActual = calcularProximaFecha(fechaActual, 1); // Busca el pr√≥ximo lunes
    } else if (diaSemana > 4) {
      fechaActual = calcularProximaFecha(fechaActual, 1); // Busca el pr√≥ximo lunes
    }
  }

  // Agregamos la fecha actual (ya ajustada)
  fechasDePublicacion.push({
    json: {
      fecha: fechaActual.toISOString().split('T')[0]
    }
  });

  // Calculamos la siguiente fecha de publicaci√≥n
  if (fechaActual.getDay() === 1) { // Si es lunes, el pr√≥ximo es jueves
    fechaActual = calcularProximaFecha(fechaActual, 4);
  } else if (fechaActual.getDay() === 4) { // Si es jueves, el pr√≥ximo es lunes
    fechaActual = calcularProximaFecha(fechaActual, 1);
  } else {
    // Caso de resguardo, pasamos al siguiente lunes.
    fechaActual = calcularProximaFecha(fechaActual, 1);
  }
}

// Devolver el resultado en el formato que n8n espera
return fechasDePublicacion;

