// La fecha de inicio debe ser un objeto Date
// Si el primer video se publica hoy, la inicializamos
let fechaActual = new Date(); 

const numeroDeVideos = 8;
const fechasDePublicacion = [];

// Esta es la funci√≥n que calcula la pr√≥xima fecha de publicaci√≥n
function calcularProximaFecha(fechaBase, diaObjetivo) {
  let fecha = new Date(fechaBase);
  const diaSemanaActual = fecha.getDay(); // 0 = Domingo, 1 = Lunes, 4 = Jueves, etc.

  // Calculamos los d√≠as que faltan para llegar al d√≠a objetivo
  let diferenciaDias = diaObjetivo - diaSemanaActual;

  // Si la fecha actual ya es posterior al d√≠a objetivo, sumamos 7 para ir a la siguiente semana
  if (diferenciaDias <= 0) {
    diferenciaDias += 7;
  }

  fecha.setDate(fecha.getDate() + diferenciaDias);
  return fecha;
}

