// Obtener el HTML del input
const html = $input.first().json.data;

const posts = [];
const postPattern = /<li class="wp-block-post[^"]*"[\s\S]*?<\/li>/g;
const postMatches = html.match(postPattern);

// Verificar que hay matches
if (postMatches && postMatches.length > 0) {
  postMatches.forEach(post => {
    const titleMatch = post.match(/<a[^>]*loop-card__title-link[^>]*href="([^"]*)"[^>]*>([\s\S]*?)<\/a>/);
    const imageMatch = post.match(/<img[^>]*wp-post-image[^>]*src="([^"]*)"[^>]*>/);
    const authorMatch = post.match(/<a[^>]*loop-card__author[^>]*href="([^"]*)"[^>]*>([^<]*)<\/a>/);
    const categoryMatch = post.match(/<a[^>]*loop-card__cat[^>]*>([^<]*)<\/a>/) || post.match(/<span class="loop-card__cat">([^<]*)<\/span>/);
    const dateMatch = post.match(/<time[^>]*datetime="([^"]*)"[^>]*>([^<]*)<\/time>/);

    if (titleMatch) {
      posts.push({
        title: titleMatch[2].replace(/<[^>]*>/g, '').replace(/&#8217;/g, "'").replace(/&#8216;/g, "'").replace(/&amp;/g, "&").trim(),
        url: titleMatch[1],
        imageUrl: imageMatch ? imageMatch[1] : null,
        author: authorMatch ? authorMatch[2].trim() : null,
        authorUrl: authorMatch ? authorMatch[1] : null,
        category: categoryMatch ? categoryMatch[1].trim() : null,
        date: dateMatch ? dateMatch[1] : null,
        relativeDate: dateMatch ? dateMatch[2].trim() : null
      });
    }
  });
}

// Si no hay posts, retornar al menos un item vacío
if (posts.length === 0) {
  return [{ json: { message: 'No posts found', posts: [] } }];
}

// Retornar cada post como un item separado
return posts.map(post => ({ json: post }));

const pagesToScrape = $input.first().json.pageToScrape;
const searchTerm = $input.first().json.searchTerm;
const pages = [];

for (let i = 1; i <= pagesToScrape; i++){
  const daysAgo = i -1;
  const since = new Date();
  since.setDate(since.getDate() - daysAgo - 1);
  const until = new Date();
  until.setDate(until.getDate() - daysAgo);
  pages.push({
    json: {
      page_number: i,
      search_term: searchTerm,
      since: since.toISOString().split('T')[0],
      until: until.toISOString().split('T')[0],
      total_pages: pagesToScrape
    }
  });
}
return pages;
